import React, { useState, useEffect } from 'react';
import './index.css';

const API_BASE = 'http://127.0.0.1:8080';
const PERIFERICO_BASE = `${API_BASE}/hcen-web/api`;
const RNDC_BASE = `${API_BASE}/rndc/api`;
const POLITICAS_BASE = `${API_BASE}/hcen-politicas-service/api`;

function App() {
  const [token, setToken] = useState(localStorage.getItem('token') || '');
  const [nickname, setNickname] = useState('admin_c1');
  const [password, setPassword] = useState('password123');
  const [loading, setLoading] = useState(false);
  const [result, setResult] = useState(null);
  const [modo, setModo] = useState('profesional'); // 'profesional' o 'paciente'

  // Documento
  const [documentoId, setDocumentoId] = useState('');
  const [mongoId, setMongoId] = useState('');
  const [documentoIdPaciente, setDocumentoIdPaciente] = useState('12345673');
  const [contenido, setContenido] = useState('Paciente presenta sintomas de gripe. Diagnostico: Gripe estacional. Tratamiento: reposo, hidratacion y medicamentos sintomaticos.');
  const [titulo, setTitulo] = useState('Consulta Medica - Gripe');
  const [especialidad, setEspecialidad] = useState('MEDICINA_GENERAL');

  // BÃºsqueda
  const [ciBusqueda, setCiBusqueda] = useState('12345673');
  const [documentosEncontrados, setDocumentosEncontrados] = useState([]);
  const [documentoSeleccionado, setDocumentoSeleccionado] = useState(null);

  // Solicitudes
  const [solicitudId, setSolicitudId] = useState('');
  const [solicitudes, setSolicitudes] = useState([]);
  const [solicitudTipoDocumento, setSolicitudTipoDocumento] = useState('');
  const [solicitudDocumentoId, setSolicitudDocumentoId] = useState('');
  const [solicitudHistoriaCompleta, setSolicitudHistoriaCompleta] = useState(false);

  useEffect(() => {
    if (token) {
      localStorage.setItem('token', token);
    } else {
      localStorage.removeItem('token');
    }
  }, [token]);

  const handleLogin = async () => {
    setLoading(true);
    setResult(null);
    try {
      const response = await fetch(`${PERIFERICO_BASE}/auth/login`, {
        method: 'POST',
        headers: { 
          'Content-Type': 'application/json',
          'Accept': 'application/json'
        },
        body: JSON.stringify({ nickname, password })
      });

      // Leer el texto primero para poder intentar parsearlo como JSON
      const text = await response.text();
      let data;
      try {
        data = JSON.parse(text);
      } catch (e) {
        // Si no se puede parsear como JSON, usar el texto como mensaje de error
        setResult({ 
          success: false, 
          message: `Error ${response.status}: ${text || response.statusText}`,
          error: `Status: ${response.status}, Body: ${text}`
        });
        setLoading(false);
        return;
      }

      if (response.ok && data.token) {
        setToken(data.token);
        setResult({ success: true, message: 'Login exitoso', data });
      } else {
        setResult({ 
          success: false, 
          message: data.error || `Error en login (${response.status})`, 
          data 
        });
      }
    } catch (error) {
      setResult({ 
        success: false, 
        message: `Error de conexiÃ³n: ${error.message}`, 
        error: error.toString() 
      });
    } finally {
      setLoading(false);
    }
  };

  const handleLogout = () => {
    setToken('');
    setResult(null);
    setDocumentosEncontrados([]);
    setDocumentoSeleccionado(null);
  };

  const handleCrearDocumento = async () => {
    if (!token) {
      setResult({ success: false, message: 'Debes iniciar sesiÃ³n primero' });
      return;
    }

    setLoading(true);
    setResult(null);
    try {
      const body = {
        documentoIdPaciente,
        contenido,
        titulo,
        especialidad,
        formato: 'text/plain',
        autor: nickname,
        fechaCreacion: new Date().toISOString(),
        breakingTheGlass: false
      };

      const response = await fetch(`${PERIFERICO_BASE}/documentos/completo`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`
        },
        body: JSON.stringify(body)
      });

      // Leer el texto primero para poder intentar parsearlo como JSON
      const text = await response.text();
      let data;
      try {
        data = JSON.parse(text);
      } catch (e) {
        // Si no se puede parsear como JSON, usar el texto como mensaje de error
        setResult({ 
          success: false, 
          message: `Error ${response.status}: ${text || response.statusText}`,
          error: `Status: ${response.status}, Body: ${text}`
        });
        setLoading(false);
        return;
      }

      if (response.ok) {
        setDocumentoId(data.documentoId || '');
        setMongoId(data.mongoId || '');
        setResult({ success: true, message: 'Documento creado exitosamente', data });
      } else {
        setResult({ success: false, message: data.error || 'Error al crear documento', data });
      }
    } catch (error) {
      setResult({ success: false, message: error.message, error: error.toString() });
    } finally {
      setLoading(false);
    }
  };

  const handleBuscarPorCI = async () => {
    if (!token) {
      setResult({ success: false, message: 'Debes iniciar sesiÃ³n primero' });
      return;
    }

    setLoading(true);
    setResult(null);
    try {
      const response = await fetch(`${RNDC_BASE}/rndc/documentos/paciente/${ciBusqueda}`, {
        method: 'GET',
        headers: {
          'X-Profesional-Id': nickname
        }
      });

      // Leer el texto primero para poder intentar parsearlo como JSON
      const text = await response.text();
      let data;
      try {
        data = JSON.parse(text);
      } catch (e) {
        // Si no se puede parsear como JSON, usar el texto como mensaje de error
        setResult({ 
          success: false, 
          message: `Error ${response.status}: ${text || response.statusText}`,
          error: `Status: ${response.status}, Body: ${text}`
        });
        setLoading(false);
        return;
      }

      if (response.ok) {
        setDocumentosEncontrados(Array.isArray(data) ? data : []);
        setResult({ success: true, message: `Encontrados ${Array.isArray(data) ? data.length : 0} documentos`, data });
      } else {
        setResult({ success: false, message: data.error || 'Error al buscar documentos', data });
      }
    } catch (error) {
      setResult({ success: false, message: error.message, error: error.toString() });
    } finally {
      setLoading(false);
    }
  };

  const handleObtenerDocumento = async (docId) => {
    if (!token) {
      setResult({ success: false, message: 'Debes iniciar sesiÃ³n primero' });
      return;
    }

    setLoading(true);
    setResult(null);
    try {
      const response = await fetch(`${RNDC_BASE}/rndc/documentos/${docId}`, {
        method: 'GET',
        headers: {
          'X-Profesional-Id': nickname
        }
      });

      // Leer el texto primero para poder intentar parsearlo como JSON
      const text = await response.text();
      let data;
      try {
        data = JSON.parse(text);
      } catch (e) {
        // Si no se puede parsear como JSON, usar el texto como mensaje de error
        setResult({ 
          success: false, 
          message: `Error ${response.status}: ${text || response.statusText}`,
          error: `Status: ${response.status}, Body: ${text}`
        });
        setLoading(false);
        return;
      }

      if (response.ok) {
        setDocumentoSeleccionado(data);
        setResult({ success: true, message: 'Documento obtenido exitosamente', data });
      } else {
        setResult({ success: false, message: data.error || 'Error al obtener documento', data });
      }
    } catch (error) {
      setResult({ success: false, message: error.message, error: error.toString() });
    } finally {
      setLoading(false);
    }
  };

  const handleDescargarDocumento = async (docId, esPaciente = false) => {
    if (!token && !esPaciente) {
      setResult({ success: false, message: 'Debes iniciar sesiÃ³n primero' });
      return;
    }

    setLoading(true);
    setResult(null);
    try {
      const headers = {};
      if (esPaciente) {
        headers['X-Paciente-CI'] = documentoIdPaciente;
      } else {
        headers['X-Profesional-Id'] = nickname;
      }

      const response = await fetch(`${RNDC_BASE}/rndc/documentos/${docId}/descargar`, {
        method: 'GET',
        headers: headers
      });

      if (response.ok) {
        const blob = await response.blob();
        const url = window.URL.createObjectURL(blob);
        const a = document.createElement('a');
        a.href = url;
        a.download = `documento-${docId}.pdf`;
        document.body.appendChild(a);
        a.click();
        window.URL.revokeObjectURL(url);
        document.body.removeChild(a);
        setResult({ success: true, message: `Documento descargado exitosamente ${esPaciente ? '(como paciente)' : '(como profesional)'}` });
      } else {
        // Leer el texto primero para poder intentar parsearlo como JSON
        const text = await response.text();
        let data;
        try {
          data = JSON.parse(text);
        } catch (e) {
          // Si no se puede parsear como JSON, usar el texto como mensaje de error
          setResult({ 
            success: false, 
            message: `Error ${response.status}: ${text || response.statusText}`,
            error: `Status: ${response.status}, Body: ${text}`
          });
          setLoading(false);
          return;
        }
        setResult({ success: false, message: data.error || 'Error al descargar documento', data });
      }
    } catch (error) {
      setResult({ success: false, message: error.message, error: error.toString() });
    } finally {
      setLoading(false);
    }
  };

  const handleDescargarContenido = async (mongoId, esPaciente = false) => {
    if (!token && !esPaciente) {
      setResult({ success: false, message: 'Debes iniciar sesiÃ³n primero' });
      return;
    }

    setLoading(true);
    setResult(null);
    try {
      const headers = {
        'Authorization': `Bearer ${token}`
      };
      if (esPaciente) {
        headers['X-Paciente-CI'] = documentoIdPaciente;
      }

      const response = await fetch(`${PERIFERICO_BASE}/documentos/${mongoId}/contenido`, {
        method: 'GET',
        headers: headers
      });

      // Leer el texto primero para poder intentar parsearlo como JSON
      const text = await response.text();
      let data;
      try {
        data = JSON.parse(text);
      } catch (e) {
        // Si no se puede parsear como JSON, puede ser texto plano
        if (response.ok) {
          setResult({ success: true, message: 'Contenido obtenido exitosamente', data: { contenido: text } });
          setLoading(false);
          return;
        }
        // Si no se puede parsear como JSON, usar el texto como mensaje de error
        setResult({ 
          success: false, 
          message: `Error ${response.status}: ${text || response.statusText}`,
          error: `Status: ${response.status}, Body: ${text}`
        });
        setLoading(false);
        return;
      }

      if (response.ok) {
        setResult({ success: true, message: `Contenido obtenido exitosamente ${esPaciente ? '(como paciente)' : '(como profesional)'}`, data });
      } else {
        setResult({ success: false, message: data.error || 'Error al obtener contenido', data });
      }
    } catch (error) {
      setResult({ success: false, message: error.message, error: error.toString() });
    } finally {
      setLoading(false);
    }
  };

  const handleListarSolicitudes = async () => {
    if (!token) {
      setResult({ success: false, message: 'Debes iniciar sesiÃ³n primero' });
      return;
    }

    setLoading(true);
    setResult(null);
    try {
      // Listar solicitudes pendientes del paciente
      const response = await fetch(`${POLITICAS_BASE}/solicitudes/paciente/${documentoIdPaciente}`, {
        method: 'GET',
        headers: {
          'Content-Type': 'application/json'
        }
      });

      const text = await response.text();
      let data;
      try {
        data = JSON.parse(text);
      } catch (e) {
        setResult({ 
          success: false, 
          message: `Error ${response.status}: ${text || response.statusText}`,
          error: `Status: ${response.status}, Body: ${text}`
        });
        setLoading(false);
        return;
      }

      if (response.ok) {
        setSolicitudes(Array.isArray(data) ? data : []);
        setResult({ success: true, message: `Encontradas ${Array.isArray(data) ? data.length : 0} solicitudes`, data });
      } else {
        setResult({ success: false, message: data.error || 'Error al listar solicitudes', data });
      }
    } catch (error) {
      setResult({ success: false, message: error.message, error: error.toString() });
    } finally {
      setLoading(false);
    }
  };

  const handleVerificarPermiso = async (docId) => {
    if (!token) {
      setResult({ success: false, message: 'Debes iniciar sesiÃ³n primero' });
      return;
    }

    setLoading(true);
    setResult(null);
    try {
      const documento = documentosEncontrados.find(d => (d.codDocum || d.id) === docId);
      const tipoDoc = documento?.tipoDocumento || '';
      
      const response = await fetch(
        `${POLITICAS_BASE}/politicas/verificar?profesionalId=${encodeURIComponent(nickname)}&pacienteCI=${encodeURIComponent(documentoIdPaciente)}&tipoDoc=${encodeURIComponent(tipoDoc)}`,
        {
          method: 'GET',
          headers: {
            'Content-Type': 'application/json'
          }
        }
      );

      const text = await response.text();
      let data;
      try {
        data = JSON.parse(text);
      } catch (e) {
        setResult({ 
          success: false, 
          message: `Error ${response.status}: ${text || response.statusText}`,
          error: `Status: ${response.status}, Body: ${text}`
        });
        setLoading(false);
        return;
      }

      if (response.ok) {
        setResult({ 
          success: true, 
          message: data.tienePermiso ? 'âœ… Tiene permiso para acceder' : 'âŒ No tiene permiso para acceder',
          data 
        });
      } else {
        setResult({ success: false, message: data.error || 'Error al verificar permiso', data });
      }
    } catch (error) {
      setResult({ success: false, message: error.message, error: error.toString() });
    } finally {
      setLoading(false);
    }
  };

  const handleSolicitarAcceso = async () => {
    if (!token) {
      setResult({ success: false, message: 'Debes iniciar sesiÃ³n primero' });
      return;
    }

    setLoading(true);
    setResult(null);
    try {
      let body;
      let endpoint;
      
      if (solicitudHistoriaCompleta) {
        // Solicitar acceso a toda la historia clÃ­nica
        endpoint = `${PERIFERICO_BASE}/documentos/solicitar-acceso-historia-completa`;
        body = {
          codDocumPaciente: documentoIdPaciente,
          razonSolicitud: 'Solicitud de acceso a toda la historia clÃ­nica del paciente',
          especialidad: especialidad
        };
      } else {
        // Solicitar acceso a documento especÃ­fico
        endpoint = `${PERIFERICO_BASE}/documentos/solicitar-acceso`;
        body = {
          codDocumPaciente: documentoIdPaciente,
          tipoDocumento: solicitudTipoDocumento || (documentoId ? 'OTROS' : null),
          documentoId: solicitudDocumentoId || documentoId || null,
          razonSolicitud: 'Prueba de acceso desde frontend'
        };
      }

      const response = await fetch(endpoint, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`
        },
        body: JSON.stringify(body)
      });

      // Leer el texto primero para poder intentar parsearlo como JSON
      const text = await response.text();
      let data;
      try {
        data = JSON.parse(text);
      } catch (e) {
        // Si no se puede parsear como JSON, usar el texto como mensaje de error
        setResult({ 
          success: false, 
          message: `Error ${response.status}: ${text || response.statusText}`,
          error: `Status: ${response.status}, Body: ${text}`
        });
        setLoading(false);
        return;
      }

      if (response.ok) {
        const solicitudIdNuevo = data.id || data.solicitudId || '';
        setSolicitudId(solicitudIdNuevo);
        setResult({ success: true, message: 'Solicitud creada exitosamente', data });
        // Actualizar lista de solicitudes
        setTimeout(() => handleListarSolicitudes(), 500);
      } else {
        setResult({ success: false, message: data.error || 'Error al crear solicitud', data });
      }
    } catch (error) {
      setResult({ success: false, message: error.message, error: error.toString() });
    } finally {
      setLoading(false);
    }
  };

  const handleAprobarSolicitud = async (solicitudId) => {
    if (!token) {
      setResult({ success: false, message: 'Debes iniciar sesiÃ³n primero' });
      return;
    }

    setLoading(true);
    setResult(null);
    try {
      const response = await fetch(`${PERIFERICO_BASE}/documentos/solicitudes/${solicitudId}/aprobar`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`
        },
        body: JSON.stringify({
          resueltoPor: modo === 'paciente' ? `paciente_${documentoIdPaciente}` : nickname,
          comentario: 'Aprobado desde frontend de pruebas'
        })
      });

      if (!response.ok) {
        const errorText = await response.text();
        let errorData;
        try {
          errorData = JSON.parse(errorText);
        } catch {
          errorData = { error: errorText || `Error ${response.status}` };
        }
        setResult({ success: false, message: errorData.error || 'Error al aprobar solicitud', data: errorData });
        return;
      }

      const data = await response.json();
      setResult({ success: true, message: 'âœ… Solicitud aprobada exitosamente. Se creÃ³ automÃ¡ticamente una polÃ­tica de acceso.', data });
      // Actualizar lista de solicitudes
      setTimeout(() => handleListarSolicitudes(), 500);
    } catch (error) {
      setResult({ success: false, message: error.message, error: error.toString() });
    } finally {
      setLoading(false);
    }
  };

  const handleRechazarSolicitud = async (solicitudId) => {
    if (!token) {
      setResult({ success: false, message: 'Debes iniciar sesiÃ³n primero' });
      return;
    }

    setLoading(true);
    setResult(null);
    try {
      const response = await fetch(`${PERIFERICO_BASE}/documentos/solicitudes/${solicitudId}/rechazar`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`
        },
        body: JSON.stringify({
          resueltoPor: modo === 'paciente' ? `paciente_${documentoIdPaciente}` : nickname,
          comentario: 'Rechazado desde frontend de pruebas'
        })
      });

      if (!response.ok) {
        const errorText = await response.text();
        let errorData;
        try {
          errorData = JSON.parse(errorText);
        } catch {
          errorData = { error: errorText || `Error ${response.status}` };
        }
        setResult({ success: false, message: errorData.error || 'Error al rechazar solicitud', data: errorData });
        return;
      }

      const data = await response.json();
      setResult({ success: true, message: 'âŒ Solicitud rechazada exitosamente', data });
      // Actualizar lista de solicitudes
      setTimeout(() => handleListarSolicitudes(), 500);
    } catch (error) {
      setResult({ success: false, message: error.message, error: error.toString() });
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="container">
      <div className="header">
        <h1>ðŸ”¬ HCEN Test Frontend</h1>
        <p>Interfaz de pruebas para endpoints HCEN - Flujo Completo de Acceso a Documentos</p>
        <div style={{ marginTop: '10px', display: 'flex', gap: '15px', alignItems: 'center', flexWrap: 'wrap' }}>
          <div>
            Estado: 
            <span className={`status-badge ${token ? 'authenticated' : 'unauthenticated'}`}>
              {token ? 'Autenticado' : 'No autenticado'}
            </span>
          </div>
          <div>
            Modo: 
            <select 
              value={modo} 
              onChange={(e) => setModo(e.target.value)}
              style={{ marginLeft: '5px', padding: '5px' }}
            >
              <option value="profesional">ðŸ‘¨â€âš•ï¸ Profesional</option>
              <option value="paciente">ðŸ‘¤ Paciente</option>
            </select>
          </div>
          {token && (
            <div className="token-display">
              Usuario: {nickname} | Token: {token.substring(0, 20)}...
            </div>
          )}
        </div>
      </div>

      {/* Login Section */}
      <div className="login-section">
        <h2>ðŸ” AutenticaciÃ³n</h2>
        <div className="grid">
          <div className="form-group">
            <label>Nickname:</label>
            <input
              type="text"
              value={nickname}
              onChange={(e) => setNickname(e.target.value)}
              placeholder="admin_c1"
            />
          </div>
          <div className="form-group">
            <label>Password:</label>
            <input
              type="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              placeholder="password123"
            />
          </div>
        </div>
        <div className="button-group">
          <button className="btn btn-primary" onClick={handleLogin} disabled={loading}>
            Login {loading && <span className="loading"></span>}
          </button>
          {token && (
            <button className="btn btn-secondary" onClick={handleLogout}>
              Logout
            </button>
          )}
        </div>
      </div>

      {/* Crear Documento */}
      <div className="section">
        <h2>ðŸ“„ Crear Documento ClÃ­nico</h2>
        <div className="grid">
          <div className="form-group">
            <label>CI del Paciente:</label>
            <input
              type="text"
              value={documentoIdPaciente}
              onChange={(e) => setDocumentoIdPaciente(e.target.value)}
              placeholder="12345678"
            />
          </div>
          <div className="form-group">
            <label>TÃ­tulo:</label>
            <input
              type="text"
              value={titulo}
              onChange={(e) => setTitulo(e.target.value)}
              placeholder="Consulta Medica - Gripe"
            />
          </div>
          <div className="form-group">
            <label>Especialidad:</label>
            <select value={especialidad} onChange={(e) => setEspecialidad(e.target.value)}>
              <option value="MEDICINA_GENERAL">Medicina General</option>
              <option value="PEDIATRIA">PediatrÃ­a</option>
              <option value="CARDIOLOGIA">CardiologÃ­a</option>
              <option value="DERMATOLOGIA">DermatologÃ­a</option>
            </select>
          </div>
        </div>
        <div className="form-group">
          <label>Contenido:</label>
          <textarea
            value={contenido}
            onChange={(e) => setContenido(e.target.value)}
            placeholder="Contenido del documento..."
          />
        </div>
        <div className="button-group">
          <button className="btn btn-success" onClick={handleCrearDocumento} disabled={loading || !token}>
            Crear Documento {loading && <span className="loading"></span>}
          </button>
        </div>
        {documentoId && (
          <div className="result success" style={{ marginTop: '10px' }}>
            <strong>Documento ID:</strong> {documentoId}
          </div>
        )}
      </div>

      {/* BÃºsqueda */}
      <div className="section">
        <h2>ðŸ” BÃºsqueda de Documentos</h2>
        <div className="form-group">
          <label>CI del Paciente:</label>
          <div style={{ display: 'flex', gap: '10px' }}>
            <input
              type="text"
              value={ciBusqueda}
              onChange={(e) => setCiBusqueda(e.target.value)}
              placeholder="12345678"
              style={{ flex: 1 }}
            />
            <button className="btn btn-primary" onClick={handleBuscarPorCI} disabled={loading || !token}>
              Buscar {loading && <span className="loading"></span>}
            </button>
          </div>
        </div>

        {documentosEncontrados.length > 0 && (
          <div style={{ marginTop: '15px' }}>
            <h3>Documentos Encontrados ({documentosEncontrados.length}):</h3>
            {documentosEncontrados.map((doc, index) => (
              <div key={index} style={{ 
                padding: '10px', 
                margin: '10px 0', 
                border: '1px solid #ddd', 
                borderRadius: '4px',
                background: '#f9f9f9'
              }}>
                <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
                  <div>
                    <strong>ID:</strong> {doc.codDocum || doc.id} <br/>
                    <strong>Paciente:</strong> {doc.codIDPaciente} <br/>
                    <strong>TÃ­tulo:</strong> {doc.tipoDocumento || doc.titulo || 'N/A'}
                  </div>
                  <div className="button-group">
                    <button 
                      className="btn btn-primary" 
                      onClick={() => handleObtenerDocumento(doc.codDocum || doc.id)}
                      disabled={loading}
                      style={{ fontSize: '12px', padding: '5px 10px' }}
                    >
                      Ver
                    </button>
                    <button 
                      className="btn btn-success" 
                      onClick={() => handleDescargarDocumento(doc.codDocum || doc.id, modo === 'paciente')}
                      disabled={loading}
                      style={{ fontSize: '12px', padding: '5px 10px' }}
                      title={modo === 'paciente' ? 'Descargar como paciente (sin verificaciÃ³n de polÃ­ticas)' : 'Descargar como profesional (requiere polÃ­ticas)'}
                    >
                      {modo === 'paciente' ? 'ðŸ“¥ Descargar (Paciente)' : 'ðŸ“¥ Descargar'}
                    </button>
                    <button 
                      className="btn btn-info" 
                      onClick={() => handleVerificarPermiso(doc.codDocum || doc.id)}
                      disabled={loading || modo === 'paciente'}
                      style={{ fontSize: '12px', padding: '5px 10px' }}
                      title="Verificar si tiene permiso para acceder"
                    >
                      ðŸ” Verificar Permiso
                    </button>
                    {(doc.uriDocumento || doc.urlAcceso) && (
                      <button 
                        className="btn btn-secondary" 
                        onClick={() => {
                          const url = doc.uriDocumento || doc.urlAcceso;
                          const mongoId = url.split('/').pop();
                          handleDescargarContenido(mongoId, modo === 'paciente');
                        }}
                        disabled={loading}
                        style={{ fontSize: '12px', padding: '5px 10px' }}
                        title={modo === 'paciente' ? 'Obtener contenido como paciente' : 'Obtener contenido como profesional'}
                      >
                        {modo === 'paciente' ? 'ðŸ“„ Contenido (Paciente)' : 'ðŸ“„ Contenido'}
                      </button>
                    )}
                  </div>
                </div>
              </div>
            ))}
          </div>
        )}
      </div>

      {/* Solicitudes de Acceso */}
      <div className="section">
        <h2>ðŸ“‹ Solicitudes de Acceso</h2>
        <div style={{ marginBottom: '15px', padding: '10px', background: '#e3f2fd', borderRadius: '4px' }}>
          <strong>Flujo:</strong> Profesional crea documento â†’ Otro profesional solicita acceso â†’ <strong>PACIENTE aprueba/rechaza</strong>
        </div>
        
        {modo === 'profesional' && (
          <>
            <h3>ðŸ‘¨â€âš•ï¸ Como Profesional: Solicitar Acceso</h3>
            <div className="grid">
              <div className="form-group">
                <label>CI del Paciente:</label>
                <input
                  type="text"
                  value={documentoIdPaciente}
                  onChange={(e) => setDocumentoIdPaciente(e.target.value)}
                  placeholder="12345673"
                />
              </div>
              <div className="form-group">
                <label>
                  <input
                    type="checkbox"
                    checked={solicitudHistoriaCompleta}
                    onChange={(e) => setSolicitudHistoriaCompleta(e.target.checked)}
                    style={{ marginRight: '5px' }}
                  />
                  Solicitar acceso a historia clÃ­nica completa
                </label>
              </div>
            </div>
            {!solicitudHistoriaCompleta && (
              <div className="grid">
                <div className="form-group">
                  <label>Tipo de Documento (opcional):</label>
                  <input
                    type="text"
                    value={solicitudTipoDocumento}
                    onChange={(e) => setSolicitudTipoDocumento(e.target.value)}
                    placeholder="OTROS"
                  />
                </div>
                <div className="form-group">
                  <label>Documento ID (opcional):</label>
                  <input
                    type="text"
                    value={solicitudDocumentoId}
                    onChange={(e) => setSolicitudDocumentoId(e.target.value)}
                    placeholder={documentoId || "UUID del documento"}
                  />
                </div>
              </div>
            )}
            <div className="button-group">
              <button className="btn btn-primary" onClick={handleSolicitarAcceso} disabled={loading || !token}>
                Solicitar Acceso {loading && <span className="loading"></span>}
              </button>
            </div>
          </>
        )}

        {modo === 'paciente' && (
          <>
            <h3>ðŸ‘¤ Como Paciente: Gestionar Solicitudes</h3>
            <div className="form-group">
              <label>Mi CI:</label>
              <input
                type="text"
                value={documentoIdPaciente}
                onChange={(e) => setDocumentoIdPaciente(e.target.value)}
                placeholder="12345673"
              />
            </div>
            <div className="button-group">
              <button className="btn btn-primary" onClick={handleListarSolicitudes} disabled={loading || !token}>
                Listar Mis Solicitudes Pendientes {loading && <span className="loading"></span>}
              </button>
            </div>
          </>
        )}

        {solicitudes.length > 0 && (
          <div style={{ marginTop: '15px' }}>
            <h3>Solicitudes Encontradas ({solicitudes.length}):</h3>
            {solicitudes.map((solicitud) => (
              <div key={solicitud.id} style={{ 
                padding: '15px', 
                margin: '10px 0', 
                border: '1px solid #ddd', 
                borderRadius: '4px',
                background: solicitud.estado === 'PENDIENTE' ? '#fff3cd' : solicitud.estado === 'APROBADA' ? '#d4edda' : '#f8d7da'
              }}>
                <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'flex-start' }}>
                  <div style={{ flex: 1 }}>
                    <strong>ID:</strong> {solicitud.id} <br/>
                    <strong>Estado:</strong> {solicitud.estado} <br/>
                    <strong>Solicitante:</strong> {solicitud.solicitanteId} <br/>
                    <strong>Paciente:</strong> {solicitud.codDocumPaciente} <br/>
                    {solicitud.tipoDocumento && <><strong>Tipo Documento:</strong> {solicitud.tipoDocumento} <br/></>}
                    {solicitud.documentoId && <><strong>Documento ID:</strong> {solicitud.documentoId} <br/></>}
                    <strong>RazÃ³n:</strong> {solicitud.razonSolicitud || 'N/A'} <br/>
                    {solicitud.fechaSolicitud && <><strong>Fecha:</strong> {new Date(solicitud.fechaSolicitud).toLocaleString()} <br/></>}
                  </div>
                  {solicitud.estado === 'PENDIENTE' && modo === 'paciente' && (
                    <div className="button-group" style={{ flexDirection: 'column', gap: '5px' }}>
                      <button 
                        className="btn btn-success" 
                        onClick={() => handleAprobarSolicitud(solicitud.id)}
                        disabled={loading}
                        style={{ fontSize: '12px', padding: '5px 10px' }}
                      >
                        âœ… Aprobar
                      </button>
                      <button 
                        className="btn btn-danger" 
                        onClick={() => handleRechazarSolicitud(solicitud.id)}
                        disabled={loading}
                        style={{ fontSize: '12px', padding: '5px 10px' }}
                      >
                        âŒ Rechazar
                      </button>
                    </div>
                  )}
                </div>
              </div>
            ))}
          </div>
        )}

        {solicitudId && solicitudes.length === 0 && (
          <div style={{ marginTop: '15px' }}>
            <div className="result success">
              <strong>Solicitud ID:</strong> {solicitudId}
            </div>
            {modo === 'paciente' && (
              <div className="button-group" style={{ marginTop: '10px' }}>
                <button 
                  className="btn btn-success" 
                  onClick={() => handleAprobarSolicitud(solicitudId)}
                  disabled={loading}
                >
                  âœ… Aprobar Solicitud {solicitudId}
                </button>
                <button 
                  className="btn btn-danger" 
                  onClick={() => handleRechazarSolicitud(solicitudId)}
                  disabled={loading}
                >
                  âŒ Rechazar Solicitud {solicitudId}
                </button>
              </div>
            )}
          </div>
        )}
      </div>

      {/* Resultado */}
      {result && (
        <div className={`result ${result.success ? 'success' : 'error'}`}>
          <strong>{result.success ? 'âœ…' : 'âŒ'} {result.message}</strong>
          {result.data && (
            <pre>{JSON.stringify(result.data, null, 2)}</pre>
          )}
          {result.error && (
            <pre>{result.error}</pre>
          )}
        </div>
      )}
    </div>
  );
}

export default App;
