import React, { useState, useEffect } from 'react';
import './index.css';

const API_BASE = 'http://127.0.0.1:8080';
const PERIFERICO_BASE = `${API_BASE}/hcen-web/api`;
const RNDC_BASE = `${API_BASE}/rndc/api`;
const POLITICAS_BASE = `${API_BASE}/hcen-politicas-service/api`;

function App() {
  const [token, setToken] = useState(localStorage.getItem('token') || '');
  const [nickname, setNickname] = useState('admin_c1');
  const [password, setPassword] = useState('password123');
  const [loading, setLoading] = useState(false);
  const [result, setResult] = useState(null);
  const [modo, setModo] = useState('profesional'); // 'profesional' o 'paciente'

  // Documento
  const [documentoId, setDocumentoId] = useState('');
  const [mongoId, setMongoId] = useState('');
  const [documentoIdPaciente, setDocumentoIdPaciente] = useState('12345673');
  const [contenido, setContenido] = useState('Paciente presenta sintomas de gripe. Diagnostico: Gripe estacional. Tratamiento: reposo, hidratacion y medicamentos sintomaticos.');
  const [titulo, setTitulo] = useState('Consulta Medica - Gripe');
  const [especialidad, setEspecialidad] = useState('MEDICINA_GENERAL');
  const [archivoAdjunto, setArchivoAdjunto] = useState(null);

  // BÃºsqueda
  const [ciBusqueda, setCiBusqueda] = useState('12345673');
  const [documentosEncontrados, setDocumentosEncontrados] = useState([]);
  const [documentoSeleccionado, setDocumentoSeleccionado] = useState(null);

  // Solicitudes
  const [solicitudId, setSolicitudId] = useState('');
  const [solicitudes, setSolicitudes] = useState([]);
  const [solicitudTipoDocumento, setSolicitudTipoDocumento] = useState('');
  const [solicitudDocumentoId, setSolicitudDocumentoId] = useState('');
  const [solicitudHistoriaCompleta, setSolicitudHistoriaCompleta] = useState(false);

  useEffect(() => {
    if (token) {
      localStorage.setItem('token', token);
    } else {
      localStorage.removeItem('token');
    }
  }, [token]);

  const handleLogin = async () => {
    setLoading(true);
    setResult(null);
    try {
      const response = await fetch(`${PERIFERICO_BASE}/auth/login`, {
        method: 'POST',
        headers: { 
          'Content-Type': 'application/json',
          'Accept': 'application/json'
        },
        body: JSON.stringify({ nickname, password })
      });

      // Leer el texto primero para poder intentar parsearlo como JSON
      const text = await response.text();
      let data;
      try {
        data = JSON.parse(text);
      } catch (e) {
        // Si no se puede parsear como JSON, usar el texto como mensaje de error
        setResult({ 
          success: false, 
          message: `Error ${response.status}: ${text || response.statusText}`,
          error: `Status: ${response.status}, Body: ${text}`
        });
        setLoading(false);
        return;
      }

      if (response.ok && data.token) {
        setToken(data.token);
        setResult({ success: true, message: 'Login exitoso', data });
      } else {
        setResult({ 
          success: false, 
          message: data.error || `Error en login (${response.status})`, 
          data 
        });
      }
    } catch (error) {
      setResult({ 
        success: false, 
        message: `Error de conexiÃ³n: ${error.message}`, 
        error: error.toString() 
      });
    } finally {
      setLoading(false);
    }
  };

  const handleLogout = () => {
    setToken('');
    setResult(null);
    setDocumentosEncontrados([]);
    setDocumentoSeleccionado(null);
  };

  const handleCrearDocumento = async () => {
    if (!token) {
      setResult({ success: false, message: 'Debes iniciar sesiÃ³n primero' });
      return;
    }

    setLoading(true);
    setResult(null);
    try {
      let response;
      let data;

      // Si hay archivo adjunto, usar el endpoint multipart
      if (archivoAdjunto) {
        const formData = new FormData();
        formData.append('contenido', contenido);
        formData.append('documentoIdPaciente', documentoIdPaciente);
        formData.append('titulo', titulo);
        formData.append('autor', nickname);
        formData.append('especialidad', especialidad);
        formData.append('archivo', archivoAdjunto);

        response = await fetch(`${PERIFERICO_BASE}/documentos/completo-con-archivo`, {
          method: 'POST',
          headers: {
            'Authorization': `Bearer ${token}`
            // No establecer Content-Type, el navegador lo harÃ¡ automÃ¡ticamente con el boundary
          },
          body: formData
        });
      } else {
        // Sin archivo, usar el endpoint JSON normal
        const body = {
          documentoIdPaciente,
          contenido,
          titulo,
          especialidad,
          formato: 'text/plain',
          autor: nickname,
          fechaCreacion: new Date().toISOString(),
          breakingTheGlass: false
        };

        response = await fetch(`${PERIFERICO_BASE}/documentos/completo`, {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
            'Authorization': `Bearer ${token}`
          },
          body: JSON.stringify(body)
        });
      }

      // Leer el texto primero para poder intentar parsearlo como JSON
      const text = await response.text();
      try {
        data = JSON.parse(text);
      } catch (e) {
        // Si no se puede parsear como JSON, usar el texto como mensaje de error
        setResult({ 
          success: false, 
          message: `Error ${response.status}: ${text || response.statusText}`,
          error: `Status: ${response.status}, Body: ${text}`
        });
        setLoading(false);
        return;
      }

      if (response.ok) {
        setDocumentoId(data.documentoId || '');
        setMongoId(data.mongoId || '');
        const mensaje = 'Documento creado exitosamente' + 
          (data.tienePdf ? ' (PDF generado)' : '') +
          (data.tieneArchivoAdjunto ? ' (Archivo adjunto guardado)' : '');
        setResult({ success: true, message: mensaje, data });
        // Limpiar el archivo seleccionado despuÃ©s de crear el documento
        setArchivoAdjunto(null);
      } else {
        setResult({ success: false, message: data.error || 'Error al crear documento', data });
      }
    } catch (error) {
      setResult({ success: false, message: error.message, error: error.toString() });
    } finally {
      setLoading(false);
    }
  };

  const handleArchivoChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      setArchivoAdjunto(file);
    } else {
      setArchivoAdjunto(null);
    }
  };

  const handleBuscarPorCI = async () => {
    if (!token) {
      setResult({ success: false, message: 'Debes iniciar sesiÃ³n primero' });
      return;
    }

    setLoading(true);
    setResult(null);
    try {
      const response = await fetch(`${RNDC_BASE}/rndc/documentos/paciente/${ciBusqueda}`, {
        method: 'GET',
        headers: {
          'X-Profesional-Id': nickname
        }
      });

      // Leer el texto primero para poder intentar parsearlo como JSON
      const text = await response.text();
      let data;
      try {
        data = JSON.parse(text);
      } catch (e) {
        // Si no se puede parsear como JSON, usar el texto como mensaje de error
        setResult({ 
          success: false, 
          message: `Error ${response.status}: ${text || response.statusText}`,
          error: `Status: ${response.status}, Body: ${text}`
        });
        setLoading(false);
        return;
      }

      if (response.ok) {
