import React, { useState, useEffect } from 'react';
import './index.css';

const API_BASE = 'http://127.0.0.1:8080';
const PERIFERICO_BASE = `${API_BASE}/hcen-web/api`;
const RNDC_BASE = `${API_BASE}/rndc/api`;

function App() {
  const [token, setToken] = useState(localStorage.getItem('token') || '');
  const [nickname, setNickname] = useState('admin_c1');
  const [password, setPassword] = useState('password123');
  const [loading, setLoading] = useState(false);
  const [result, setResult] = useState(null);

  // Documento
  const [documentoId, setDocumentoId] = useState('');
  const [documentoIdPaciente, setDocumentoIdPaciente] = useState('12345678');
  const [contenido, setContenido] = useState('Paciente presenta sintomas de gripe. Diagnostico: Gripe estacional. Tratamiento: reposo, hidratacion y medicamentos sintomaticos.');
  const [titulo, setTitulo] = useState('Consulta Medica - Gripe');
  const [especialidad, setEspecialidad] = useState('MEDICINA_GENERAL');

  // B√∫squeda
  const [ciBusqueda, setCiBusqueda] = useState('12345678');
  const [documentosEncontrados, setDocumentosEncontrados] = useState([]);
  const [documentoSeleccionado, setDocumentoSeleccionado] = useState(null);

  // Solicitudes
  const [solicitudId, setSolicitudId] = useState('');
  const [solicitudes, setSolicitudes] = useState([]);

  useEffect(() => {
    if (token) {
      localStorage.setItem('token', token);
    } else {
      localStorage.removeItem('token');
    }
  }, [token]);

  const handleLogin = async () => {
    setLoading(true);
    setResult(null);
    try {
      const response = await fetch(`${PERIFERICO_BASE}/auth/login`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ nickname, password })
      });

      const data = await response.json();
      if (response.ok && data.token) {
        setToken(data.token);
        setResult({ success: true, message: 'Login exitoso', data });
      } else {
        setResult({ success: false, message: data.error || 'Error en login', data });
      }
    } catch (error) {
      setResult({ success: false, message: error.message, error: error.toString() });
    } finally {
      setLoading(false);
    }
  };

  const handleLogout = () => {
    setToken('');
    setResult(null);
    setDocumentosEncontrados([]);
    setDocumentoSeleccionado(null);
  };

  const handleCrearDocumento = async () => {
    if (!token) {
      setResult({ success: false, message: 'Debes iniciar sesi√≥n primero' });
      return;
    }

    setLoading(true);
    setResult(null);
    try {
      const body = {
        documentoIdPaciente,
        contenido,
        titulo,
        especialidad,
        formato: 'text/plain',
        autor: nickname,
        fechaCreacion: new Date().toISOString(),
        breakingTheGlass: false
      };

      const response = await fetch(`${PERIFERICO_BASE}/documentos/completo`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`
        },
        body: JSON.stringify(body)
      });

      const data = await response.json();
      if (response.ok) {
        setDocumentoId(data.documentoId || '');
        setResult({ success: true, message: 'Documento creado exitosamente', data });
      } else {
        setResult({ success: false, message: data.error || 'Error al crear documento', data });
      }
    } catch (error) {
      setResult({ success: false, message: error.message, error: error.toString() });
    } finally {
      setLoading(false);
    }
  };

  const handleBuscarPorCI = async () => {
    if (!token) {
      setResult({ success: false, message: 'Debes iniciar sesi√≥n primero' });
      return;
    }

    setLoading(true);
    setResult(null);
    try {
      const response = await fetch(`${RNDC_BASE}/rndc/documentos/paciente/${ciBusqueda}`, {
        method: 'GET',
        headers: {
          'X-Profesional-Id': nickname
        }
      });

      const data = await response.json();
      if (response.ok) {
        setDocumentosEncontrados(Array.isArray(data) ? data : []);
        setResult({ success: true, message: `Encontrados ${Array.isArray(data) ? data.length : 0} documentos`, data });
      } else {
        setResult({ success: false, message: data.error || 'Error al buscar documentos', data });
      }
    } catch (error) {
      setResult({ success: false, message: error.message, error: error.toString() });
    } finally {
      setLoading(false);
    }
  };

  const handleObtenerDocumento = async (docId) => {
    if (!token) {
      setResult({ success: false, message: 'Debes iniciar sesi√≥n primero' });
      return;
    }

    setLoading(true);
    setResult(null);
    try {
      const response = await fetch(`${RNDC_BASE}/rndc/documentos/${docId}`, {
        method: 'GET',
        headers: {
          'X-Profesional-Id': nickname
        }
      });

      const data = await response.json();
      if (response.ok) {
        setDocumentoSeleccionado(data);
        setResult({ success: true, message: 'Documento obtenido exitosamente', data });
      } else {
        setResult({ success: false, message: data.error || 'Error al obtener documento', data });
      }
    } catch (error) {
      setResult({ success: false, message: error.message, error: error.toString() });
    } finally {
      setLoading(false);
    }
  };

  const handleDescargarDocumento = async (docId) => {
    if (!token) {
      setResult({ success: false, message: 'Debes iniciar sesi√≥n primero' });
      return;
    }

    setLoading(true);
    setResult(null);
    try {
      const response = await fetch(`${RNDC_BASE}/rndc/documentos/${docId}/descargar`, {
        method: 'GET',
        headers: {
          'X-Profesional-Id': nickname
        }
      });

      if (response.ok) {
        const blob = await response.blob();
        const url = window.URL.createObjectURL(blob);
        const a = document.createElement('a');
        a.href = url;
        a.download = `documento-${docId}.pdf`;
        document.body.appendChild(a);
        a.click();
        window.URL.revokeObjectURL(url);
        document.body.removeChild(a);
        setResult({ success: true, message: 'Documento descargado exitosamente' });
      } else {
        const data = await response.json();
        setResult({ success: false, message: data.error || 'Error al descargar documento', data });
      }
    } catch (error) {
      setResult({ success: false, message: error.message, error: error.toString() });
    } finally {
      setLoading(false);
    }
  };

  const handleDescargarContenido = async (mongoId) => {
    if (!token) {
      setResult({ success: false, message: 'Debes iniciar sesi√≥n primero' });
      return;
    }

    setLoading(true);
    setResult(null);
    try {
      const response = await fetch(`${PERIFERICO_BASE}/documentos/${mongoId}/contenido`, {
        method: 'GET',
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });

      if (response.ok) {
        const data = await response.json();
        setResult({ success: true, message: 'Contenido obtenido exitosamente', data });
      } else {
        const data = await response.json();
        setResult({ success: false, message: data.error || 'Error al obtener contenido', data });
      }
    } catch (error) {
      setResult({ success: false, message: error.message, error: error.toString() });
    } finally {
      setLoading(false);
    }
  };

  const handleSolicitarAcceso = async () => {
    if (!token) {
      setResult({ success: false, message: 'Debes iniciar sesi√≥n primero' });
      return;
    }

    setLoading(true);
    setResult(null);
    try {
      const body = {
        documentoIdPaciente,
        tipoSolicitud: documentoId ? 'DOCUMENTO_ESPECIFICO' : 'HISTORIA_CLINICA_COMPLETA',
        documentoId: documentoId || null,
        motivo: 'Prueba de acceso desde frontend'
      };

      const response = await fetch(`${PERIFERICO_BASE}/documentos/solicitar-acceso`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`
        },
        body: JSON.stringify(body)
      });

      const data = await response.json();
      if (response.ok) {
        setSolicitudId(data.id || data.solicitudId || '');
        setResult({ success: true, message: 'Solicitud creada exitosamente', data });
      } else {
        setResult({ success: false, message: data.error || 'Error al crear solicitud', data });
      }
    } catch (error) {
      setResult({ success: false, message: error.message, error: error.toString() });
    } finally {
      setLoading(false);
    }
  };

  const handleAprobarSolicitud = async (solicitudId) => {
    if (!token) {
      setResult({ success: false, message: 'Debes iniciar sesi√≥n primero' });
      return;
    }

    setLoading(true);
    setResult(null);
    try {
      const response = await fetch(`${PERIFERICO_BASE}/documentos/solicitudes/${solicitudId}/aprobar`, {
        method: 'POST',
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });

      const data = await response.json();
      if (response.ok) {
        setResult({ success: true, message: 'Solicitud aprobada exitosamente', data });
      } else {
        setResult({ success: false, message: data.error || 'Error al aprobar solicitud', data });
      }
    } catch (error) {
      setResult({ success: false, message: error.message, error: error.toString() });
    } finally {
      setLoading(false);
    }
  };

  const handleRechazarSolicitud = async (solicitudId) => {
    if (!token) {
      setResult({ success: false, message: 'Debes iniciar sesi√≥n primero' });
      return;
    }

    setLoading(true);
    setResult(null);
    try {
      const response = await fetch(`${PERIFERICO_BASE}/documentos/solicitudes/${solicitudId}/rechazar`, {
        method: 'POST',
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });

      const data = await response.json();
      if (response.ok) {
        setResult({ success: true, message: 'Solicitud rechazada exitosamente', data });
      } else {
        setResult({ success: false, message: data.error || 'Error al rechazar solicitud', data });
      }
    } catch (error) {
      setResult({ success: false, message: error.message, error: error.toString() });
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="container">
      <div className="header">
        <h1>üî¨ HCEN Test Frontend</h1>
        <p>Interfaz de pruebas para endpoints HCEN</p>
        <div style={{ marginTop: '10px' }}>
          Estado: 
          <span className={`status-badge ${token ? 'authenticated' : 'unauthenticated'}`}>
            {token ? 'Autenticado' : 'No autenticado'}
          </span>
          {token && (
            <div className="token-display">
              Token: {token.substring(0, 30)}...
            </div>
          )}
        </div>
      </div>

      {/* Login Section */}
      <div className="login-section">
        <h2>üîê Autenticaci√≥n</h2>
        <div className="grid">
          <div className="form-group">
            <label>Nickname:</label>
            <input
              type="text"
              value={nickname}
              onChange={(e) => setNickname(e.target.value)}
              placeholder="admin_c1"
            />
          </div>
          <div className="form-group">
            <label>Password:</label>
            <input
              type="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              placeholder="password123"
            />
          </div>
        </div>
        <div className="button-group">
          <button className="btn btn-primary" onClick={handleLogin} disabled={loading}>
            Login {loading && <span className="loading"></span>}
          </button>
          {token && (
            <button className="btn btn-secondary" onClick={handleLogout}>
              Logout
            </button>
          )}
        </div>
      </div>

      {/* Crear Documento */}
      <div className="section">
        <h2>üìÑ Crear Documento Cl√≠nico</h2>
        <div className="grid">
          <div className="form-group">
            <label>CI del Paciente:</label>
            <input
              type="text"
              value={documentoIdPaciente}
              onChange={(e) => setDocumentoIdPaciente(e.target.value)}
              placeholder="12345678"
            />
          </div>
          <div className="form-group">
            <label>T√≠tulo:</label>
            <input
              type="text"
              value={titulo}
              onChange={(e) => setTitulo(e.target.value)}
              placeholder="Consulta Medica - Gripe"
            />
          </div>
          <div className="form-group">
            <label>Especialidad:</label>
            <select value={especialidad} onChange={(e) => setEspecialidad(e.target.value)}>
              <option value="MEDICINA_GENERAL">Medicina General</option>
              <option value="PEDIATRIA">Pediatr√≠a</option>
              <option value="CARDIOLOGIA">Cardiolog√≠a</option>
              <option value="DERMATOLOGIA">Dermatolog√≠a</option>
            </select>
          </div>
        </div>
        <div className="form-group">
          <label>Contenido:</label>
          <textarea
            value={contenido}
            onChange={(e) => setContenido(e.target.value)}
            placeholder="Contenido del documento..."
          />
        </div>
        <div className="button-group">
          <button className="btn btn-success" onClick={handleCrearDocumento} disabled={loading || !token}>
            Crear Documento {loading && <span className="loading"></span>}
          </button>
        </div>
        {documentoId && (
          <div className="result success" style={{ marginTop: '10px' }}>
            <strong>Documento ID:</strong> {documentoId}
          </div>
        )}
      </div>

      {/* B√∫squeda */}
      <div className="section">
        <h2>üîç B√∫squeda de Documentos</h2>
        <div className="form-group">
          <label>CI del Paciente:</label>
          <div style={{ display: 'flex', gap: '10px' }}>
            <input
              type="text"
              value={ciBusqueda}
              onChange={(e) => setCiBusqueda(e.target.value)}
              placeholder="12345678"
              style={{ flex: 1 }}
            />
            <button className="btn btn-primary" onClick={handleBuscarPorCI} disabled={loading || !token}>
              Buscar {loading && <span className="loading"></span>}
            </button>
          </div>
        </div>

        {documentosEncontrados.length > 0 && (
          <div style={{ marginTop: '15px' }}>
            <h3>Documentos Encontrados ({documentosEncontrados.length}):</h3>
            {documentosEncontrados.map((doc, index) => (
              <div key={index} style={{ 
                padding: '10px', 
                margin: '10px 0', 
                border: '1px solid #ddd', 
                borderRadius: '4px',
                background: '#f9f9f9'
              }}>
                <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
                  <div>
                    <strong>ID:</strong> {doc.codDocum || doc.id} <br/>
                    <strong>Paciente:</strong> {doc.codIDPaciente} <br/>
                    <strong>T√≠tulo:</strong> {doc.tipoDocumento || doc.titulo || 'N/A'}
                  </div>
                  <div className="button-group">
                    <button 
                      className="btn btn-primary" 
                      onClick={() => handleObtenerDocumento(doc.codDocum || doc.id)}
                      disabled={loading}
                      style={{ fontSize: '12px', padding: '5px 10px' }}
                    >
                      Ver
                    </button>
                    <button 
                      className="btn btn-success" 
                      onClick={() => handleDescargarDocumento(doc.codDocum || doc.id)}
                      disabled={loading}
                      style={{ fontSize: '12px', padding: '5px 10px' }}
                    >
                      Descargar
                    </button>
                    {(doc.uriDocumento || doc.urlAcceso) && (
                      <button 
                        className="btn btn-secondary" 
                        onClick={() => {
                          const url = doc.uriDocumento || doc.urlAcceso;
                          const mongoId = url.split('/').pop();
                          handleDescargarContenido(mongoId);
                        }}
                        disabled={loading}
                        style={{ fontSize: '12px', padding: '5px 10px' }}
                      >
                        Contenido
                      </button>
                    )}
                  </div>
                </div>
              </div>
            ))}
          </div>
        )}
      </div>

      {/* Solicitudes de Acceso */}
      <div className="section">
        <h2>üìã Solicitudes de Acceso</h2>
        <div className="grid">
          <div className="form-group">
            <label>CI del Paciente:</label>
            <input
              type="text"
              value={documentoIdPaciente}
              onChange={(e) => setDocumentoIdPaciente(e.target.value)}
              placeholder="12345678"
            />
          </div>
          <div className="form-group">
            <label>Documento ID (opcional, dejar vac√≠o para historia completa):</label>
            <input
              type="text"
              value={documentoId}
              onChange={(e) => setDocumentoId(e.target.value)}
              placeholder="UUID del documento"
            />
          </div>
        </div>
        <div className="button-group">
          <button className="btn btn-primary" onClick={handleSolicitarAcceso} disabled={loading || !token}>
            Solicitar Acceso {loading && <span className="loading"></span>}
          </button>
        </div>
        {solicitudId && (
          <div style={{ marginTop: '15px' }}>
            <div className="button-group">
              <button 
                className="btn btn-success" 
                onClick={() => handleAprobarSolicitud(solicitudId)}
                disabled={loading}
              >
                Aprobar Solicitud {solicitudId}
              </button>
              <button 
                className="btn btn-danger" 
                onClick={() => handleRechazarSolicitud(solicitudId)}
                disabled={loading}
              >
                Rechazar Solicitud {solicitudId}
              </button>
            </div>
          </div>
        )}
      </div>

      {/* Resultado */}
      {result && (
        <div className={`result ${result.success ? 'success' : 'error'}`}>
          <strong>{result.success ? '‚úÖ' : '‚ùå'} {result.message}</strong>
          {result.data && (
            <pre>{JSON.stringify(result.data, null, 2)}</pre>
          )}
          {result.error && (
            <pre>{result.error}</pre>
          )}
        </div>
      )}
    </div>
  );
}

export default App;
